     for (let week=1;week<=52;week++){
       for (let year=1;year<=100;year++){
        ctx.beginPath()
        ctx.arc(10+13*week, 10+8.3*year, 3, 0, 2*Math.PI)
        ctx.fill()
       }
     }
  }



